import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { ReactComponent as Logo } from '../../media/logo.svg';
import { ReactComponent as LogoWhite } from '../../media/logoWhite.svg';
import { Layout, Drawer } from 'antd';
import './Sider.css';

const { Sider } = Layout;
export class SiderBlock extends Component {
  state = { visible: false, placement: 'left' };

  showDrawer = () => {
    this.setState({
      visible: true,
    });
  };

  onClose = () => {
    this.setState({
      visible: false,
    });
  };

  onChange = (e) => {
    this.setState({
      placement: e.target.value,
    });
  };
  render() {
    const { placement, visible } = this.state;
    return (
      <>
        <Sider style={{ height: '100vh' }}>
          <div style={{ textAlign: 'center' }}>
            {/* <img src={Logo} alt="MyPlanetDesign Logo" /> */}
            <Logo className="logo" />
          </div>
          <div className="rotateMenu">
            <span
              style={{
                width: '50px',
                height: '3px',
                display: 'inline-block',
                backgroundColor: 'black',
              }}></span>
            <span
              className="rotateMenu-text"
              style={{ display: 'inline-block', padding: '0px 20px' }}
              onClick={this.showDrawer}>
              Home
            </span>
            <span
              style={{
                justifyContent: 'center',
                alignItems: 'center',
                width: '50px',
                height: '3px',
                display: 'inline-block',
                backgroundColor: 'black',
                textAlign: 'center',
              }}></span>
          </div>
        </Sider>
        <Drawer
          placement={placement}
          closable={false}
          onClose={this.onClose}
          visible={visible}
          key={placement}>
          <div className="menuFirst">
            <div>
              <LogoWhite className="logo" />
              <h2 className="menuH2 active">Home</h2>
              <h2 className="menuH2" onClick={window.fullpage_api.moveTo(2)}>About</h2>
              <h2 className="menuH2">Services</h2>
              <h2 className="menuH2">Portfolio</h2>
              <h2 className="menuH2">Trusted</h2>
              <h2 className="menuH2">Team</h2>
              <h2 className="menuH2">Contact</h2>
              <Link to="/AdminPage">Админка</Link>
            </div>
          </div>
        </Drawer>
      </>
    );
  }
}

export default SiderBlock;
